import React from 'react';
import ReactDOM from 'react-dom';
import './site.css';
//import App from './App';
import * as serviceWorker from './serviceWorker';
//import ReactMapboxGl, { Layer, Feature } from 'react-mapbox-gl';
//import { csv2geojson } from 'csv2geojson';
import mapboxgl from 'mapbox-gl';
import fs from 'fs';
// import path from 'path';
//import data from 'data.geojson';

// let data = JSON.parse(
//   '/Users/vchaudhr/Desktop/Stanford/448/hw3/SBFood/src/data/data.geojson',
//   'utf8'
// );
// console.log(data);

mapboxgl.accessToken =
  'pk.eyJ1IjoidmVkaWNoYXVkaHJpIiwiYSI6ImNrNjM2NGpxYjAxaG8zbW1weTNuMmxydDkifQ.2Va6SpC54bm8IkG0SGZ8lw';

class Application extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: -122.1430195,
      lat: 37.4418834,
      zoom: 9
    };
  }

  componentDidMount() {
    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom
    });
    map.on('move', () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      });
    });
    map.addSource('map-data', {
      type: 'geojson',
      data:
        'https://github.com/vedichaudhri/SouthBayRestaurantsMap/blob/master/src/data.geojson',
      buffer: 0,
      maxzoom: 12
    });

    map.addLayer(
      {
        id: 'map',
        type: 'circle',
        source: 'map-data',
        paint: {
          'circle-color': {
            property: 'CYC_INJ',
            type: 'interval',
            stops: [
              [1, 'orange'],
              [2, 'red']
            ]
          },
          'circle-radius': {
            property: 'CYC_INJ',
            base: 3,
            type: 'interval',
            stops: [
              [1, 3],
              [2, 8],
              [3, 12]
            ]
          },
          'circle-opacity': 0.8,
          'circle-blur': 0.5
        },
        filter: ['>=', 'CYC_INJ', 1]
      },
      'waterway-label'
    );
  }

  render() {
    return (
      <div>
        <div className="sidebarStyle">
          <div>
            Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom:{' '}
            {this.state.zoom}
          </div>
        </div>
        <div ref={el => (this.mapContainer = el)} className="mapContainer" />
      </div>
    );
  }
}

ReactDOM.render(<Application />, document.getElementById('app'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
